namespace Ax.Fw.MetroFramework;

internal static class Resources
{
    private const string p_dialogError = "";
    public static readonly Lazy<Image> DialogError = new(() =>
    {
        using (var memoryStream = new MemoryStream(Convert.FromBase64String(p_dialogError)))
            return Image.FromStream(memoryStream);
    });

    private const string p_dialogInfo = "";
    public static readonly Lazy<Image> DialogInfo = new(() =>
    {
        using (var memoryStream = new MemoryStream(Convert.FromBase64String(p_dialogInfo)))
            return Image.FromStream(memoryStream);
    });

    private const string p_dialogWarning = "";
    public static readonly Lazy<Image> DialogWarning = new(() =>
    {
        using (var memoryStream = new MemoryStream(Convert.FromBase64String(p_dialogWarning)))
            return Image.FromStream(memoryStream);
    });

}
